import networkx as nx

# Create an icosahedral graph
G = nx.icosahedral_graph()

# Compute all pairs of node connectivity
connectivity = dict(nx.all_pairs_node_connectivity(G))

# Find unconnected pairs and their connectivity
independent_paths = {}
for node1 in G.nodes():
    for node2 in G.nodes():
        if node1 != node2 and not G.has_edge(node1, node2):
            independent_paths[(node1, node2)] = connectivity[node1][node2]

# Result is a dictionary of (node1, node2) pairs to their independent pathways count
result = independent_paths

import networkx as nx

# Create a path graph with 4 frames (0 to 3)
G = nx.path_graph(4)

# Bipartite Coloring: Assign colors alternately (0 and 1)
colors = {i: i % 2 for i in G.nodes()}

# Depth-limited DFS from a specific starting frame (e.g., frame 0) with a maximum depth of 2
starting_frame = 0
max_depth = 2

def depth_limited_dfs(G, node, depth):
    if depth == 0:
        return [node]
    return [node] + [n for neighbor in G.neighbors(node) 
                     for n in depth_limited_dfs(G, neighbor, depth - 1)]

dfs_result = depth_limited_dfs(G, starting_frame, max_depth)

result = {
    'colors': colors,
    'dfs_traversal': dfs_result
}

import networkx as nx
import numpy as np
from itertools import combinations

# Parameters
num_musicians = 500
p_jam = 0.2

# Create a random graph for the musicians
adj_mtx = np.random.rand(num_musicians, num_musicians)
adj_mtx = (adj_mtx < p_jam).astype(int)
np.fill_diagonal(adj_mtx, 0)  # No self loops
G = nx.from_numpy_array(adj_mtx)

# Function to count triangles (3-node motifs)
def count_triangles(graph):
    triangle_count = {}
    for u, v, w in combinations(graph.nodes(), 3):
        if graph.has_edge(u, v) and graph.has_edge(v, w) and graph.has_edge(u, w):
            for node in (u, v, w):
                if node not in triangle_count:
                    triangle_count[node] = 0
                triangle_count[node] += 1
    return triangle_count

# Count triangles in the graph
motifs_count = count_triangles(G)

# Generate a list of memberships based on identified motifs
memberships = {}
for node, count in motifs_count.items():
    if count not in memberships:
        memberships[count] = []
    memberships[count].append(node)

# Convert memberships to a sorted dictionary of members
result = {k: memberships[k] for k in sorted(memberships.keys())}
